                    $this->db->where('id', $sql_pas->id)->update('tbl_m_pasien', $data_pas);
                    $last_id = $sql_pas->id;
                }
                
                # Config File Foto Pasien
                $kode               = sprintf('%05d', $last_id);
                $no_rm              = strtolower($pengaturan->kode_pasien).$kode;
                $path               = 'file/pasien/'.$no_rm.'/';
                
