                    $this->session->set_flashdata('medcheck_toast', 'toastr.error("Nama ruang / kamar harus diisi !");');
                }

                $this->session->set_flashdata('form_error', $msg_error);

                redirect(base_url('medcheck/transfer.php?id='.$id));
