            // header('Content-Type: application/vnd.openxmlformats-officedocument.spreadsheetml.sheet');
            header('Content-Disposition: attachment;filename="label_'.$no_res.'_'.str_replace(array(' ','.'),'_', strtolower($sql_pasien->nama)).'.xls"');

